import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";


function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState('2019');

  function filterChangeHandler(selectedYear) {
    setFilteredYear(selectedYear);
  }

  const filteredExpenses = props.item.filter(expense => {
    return expense.date.getFullYear().toString() === filteredYear; // compares true or false if the date from array matches the "filteredYear"
  });

  let expensesMessage = <p>No Expenses in this year.</p>; 

  if (filteredExpenses.lenght >0) {
    expensesMessage = filteredExpenses.map( (expense) => (
      <ExpenseItem 
        key={expense.id}       // always add key when mapping lists/arrays
        title={expense.title} 
        amount = {expense.amount} 
        date = {expense.date} 
      /> 
    ))
  }
  
      return (

        <div>

          <Card className = "expenses">

          <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />

          { expensesMessage }

          </Card>

        </div>
      );
};

export default Expenses;
