import React, { useState } from 'react';

import "./ExpenseForm.css";

function ExpenseForm() {
    const [enteredTitle, updatedTitle] = useState(welcome);
    const [enteredAmount, updatedAmount] = useState('');
    const [enteredDate, updatedDate] = useState('');
    // const [ userInput, updatedInput ] = useState({
    //     enteredTitle : '',
    //     enteredAmount : '',
    //     enteredDate : '',
    // });

    function titleChangeHandler(event) {
        updatedTitle(event.target.value);
        // updatedInput ({
        //     ...userInput,
        //     enteredTitle : event.target.value,
        // })
    }

    function amountChangeHandler(event) {
        updatedAmount(event.target.value);
        // updatedInput ({
        //     ...userInput,
        //     enteredAmount : event.target.value,
        // })
    }

    function dateChangeHandler(event) {
        updatedDate(event.target.value);
        // updatedInput ({
        //     ...userInput,
        //     enteredDate : date.target.value,
        // })
    }

    function submitHandler(event) {
        event.preventDefault();

        const expenseData = { 
            title : enteredTitle,
            amount : enteredAmount,
            date : new Date(enteredDate),
        };

        console.log(expenseData);
        updatedTitle('');
        updatedAmount('');
        updatedDate('');
    }


    return (
        <form onSubmit = { submitHandler }>
            <div className = "new-expense__controls">

                <div className = "new-expense__control">
                    <label> Title </label>
                    <input type = "text" onChange = { titleChangeHandler }/>
                </div>

                <div className = "new-expense__control">
                    <label> Amount </label>
                    <input type = "number" min="0.01" step="0.01" value = { enteredAmount } onChange={ amountChangeHandler }/>
                </div>

                <div className = "new-expense__control">
                    <label> Date </label>
                    <input type = "date" min="2019-01-01" max="2022-12-31" value = { enteredDate } onChange={ dateChangeHandler }/>
                </div>

            </div>

            <div className="new-expense__actions">
                    <button type = "submit"> Add Expense </button>
                </div>

        </form>
    )
}

export default ExpenseForm;
