import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";


function NewExpense( props ) {

    function onSaveExpenseDataHandler(enteredExpensedata) {
        const expenseData = {
            ...enteredExpensedata,  // extracting data from object and appending random id
            id : Math.random().toString()
        };
        props.onAddExpense(expenseData);  // data passed in App.js component
        console.log(expenseData);
    };


    let addExpense = <button>Add New Expense</button>;

    if (addExpense.onClick === true) {
        addExpense = <ExpenseForm onSaveExpenseData = { onSaveExpenseDataHandler } />
    }

    return (
        <div className = "new-expense">

            
            {/* <ExpenseForm onSaveExpenseData = { onSaveExpenseDataHandler } />    data from ExpensesForm.js */}

            {/* <button onClick={addExpenseClickHandler}>Add New Expense</button> */}

            {addExpense}

            
        </div>
    )
} 


export default NewExpense;
