import React, { useState } from 'react';

import "./ExpenseForm.css";

function ExpenseForm() {
    const [enteredTitle, updatedTitle] = useState('');
    const [enteredamount, updatedAmount] = useState('');
    const [enteredDate, updatedDate] = useState('');
    // const [ userInput, updatedInput ] = useState({
    //     enteredTitle : '',
    //     enteredAmount : '',
    //     enteredDate : '',
    // });

    function titleChangeHandler(event) {
        updatedTitle(event.target.value);
        // updatedInput ({
        //     ...userInput,
        //     enteredTitle : event.target.value,
        // })
    }

    function amountChangeHandler(amount) {
        updatedAmount(amount.target.value);
        // updatedInput ({
        //     ...userInput,
        //     enteredAmount : event.target.value,
        // })
    }

    function dateChangeHandler(date) {
        updatedDate(date.target.value);
        // updatedInput ({
        //     ...userInput,
        //     enteredDate : date.target.value,
        // })
    }

    return (
        <form>
            <div className = "new-expense__controls">

                <div className = "new-expense__control">
                    <label> Title </label>
                    <input type = "text" onChange = { titleChangeHandler }/>
                </div>

                <div className = "new-expense__control">
                    <label> Amount </label>
                    <input type = "number" min="0.01" step="0.01" onChange={ amountChangeHandler }/>
                </div>

                <div className = "new-expense__control">
                    <label> Date </label>
                    <input type = "date" min="2019-01-01" max="2022-12-31" onChange={ dateChangeHandler }/>
                </div>

            </div>

            <div className="new-expense__actions">
                    <button type = "submit"> Add Expense </button>
                </div>

        </form>
    )
}

export default ExpenseForm;
